package AddressBook;import javax.swing.JFrame;import javax.swing.JOptionPane;public class Person{	private String firstName;	private String lastName = "";	private String address = "";	private String city = "";	private String state = "";	private String zip = "";	private String phoneNumber = "";		public Person(){		this.firstName = "Unknown";	}		public Person(String _firstName, String _lastName, String _address, String _city, String _state, String _zip, String _phoneNumber){		this.firstName =_firstName;		this.lastName = _lastName;		this.address = _address;		this.city = _city;		this.state = _state;		this.zip = _zip;		this.phoneNumber = _phoneNumber;	}		public void setLastName(String _lastName){		this.lastName = _lastName;	}		public String getLastName(){		return this.lastName;	}		public void setAddress(String _address){		this.address = _address;	}		public String getAddress(){		return this.address;	}		public void setCity(String _city){		this.city = _city;	}		public String getCity(){		return this.city;	}		public void setState(String _state){		this.state = _state;	}		public String getState(){		return this.state;	}		public void setZip(String _zip){		this.zip = _zip;	}		public String getZip(){		return this.zip;	}		public void setPhoneNumber(String _phoneNumber){		this.phoneNumber = _phoneNumber;	}		public String getPhoneNumber(){		return this.phoneNumber;	}	public String getFirstName(){		return this.firstName;	}			public String showPhoneNumber(){		return "";	}		public static boolean isPhoneNumberValid(String phoneNumber){		String pattern = "[0-9\\-\\+]+";		if(phoneNumber.matches(pattern)){			return true;		}else{			return false;		}	}		public static void showErrorMessage(JFrame frame, String message){		JOptionPane.showMessageDialog(null, message, "Error", JOptionPane.ERROR_MESSAGE);	}		public static void showSuccessMessage(JFrame frame, String message){		JOptionPane.showMessageDialog(null, message, "Success", JOptionPane.PLAIN_MESSAGE);	}		public static void main(String[] args){		Person person = new Person();			}}